package com.example.basic_practice.helper;import android.content.ContentValues;import android.content.Context;import android.database.Cursor;import android.database.sqlite.SQLiteDatabase;import android.os.Message;import androidx.annotation.NonNull;public class DatabaseManegar {    private DatabaseHelper dbhelper;    public DatabaseManegar(Context context) {        dbhelper = new DatabaseHelper(context);    }    /*     * insere data in the database     * */    public long insertData(@NonNull Messages messages) {        SQLiteDatabase db = dbhelper.getWritableDatabase();        ContentValues values = new ContentValues();        values.put("title", messages.getTitle());        values.put("body", messages.getBody());        long id = db.insert("note", null, values);        db.close();        return id;    }    /*     * fach data from the database     * */    public Cursor feachData() {        SQLiteDatabase db = dbhelper.getReadableDatabase();        String[] columns = {"id", "title", "body"};        Cursor cursor = db.query("note", columns, null, null, null, null, null);        return cursor;    }    /*     * update data in database     * */    public void updateData(@NonNull Messages messages) {        SQLiteDatabase db = dbhelper.getWritableDatabase();        ContentValues values = new ContentValues();        values.put("title", messages.getTitle());        values.put("body", messages.getBody());        db.update("note", values, "id = ?", new String[]{String.valueOf(messages.getId())});        db.close();    }    /*     * delete data from database     * */    public void deletData(String id) {        SQLiteDatabase db = dbhelper.getWritableDatabase();        db.delete("note", "id=?", new String[]{String.valueOf(id)});    }}