package com.example.basic_practice.helper;import android.content.Context;import android.content.DialogInterface;import android.content.Intent;import android.text.Layout;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.cardview.widget.CardView;import androidx.recyclerview.widget.RecyclerView;import com.example.basic_practice.R;import java.util.ArrayList;import com.example.basic_practice.*;import androidx.appcompat.app.AlertDialog;import androidx.appcompat.app.AppCompatActivity;public class Message_Adapter extends RecyclerView.Adapter<Message_Adapter.Message_ViewHolder> {    Context context;    ArrayList<Messages> messages_arrayList;    public Message_Adapter(Context context, ArrayList<Messages> messages_arrayList) {        this.context = context;        this.messages_arrayList = messages_arrayList;    }    @NonNull    @Override    public Message_ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(context).inflate(R.layout.message_layout, parent, false);        return new Message_ViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull Message_ViewHolder holder, int position) {        Messages messages = messages_arrayList.get(position);        holder.Title.setText(messages.getTitle());        holder.Description.setText(messages.getBody());        holder.Description.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(context, AddMessageActivity.class);                intent.putExtra("title", messages.getTitle());                intent.putExtra("description", messages.getBody());                intent.putExtra("id", messages.getId());                context.startActivity(intent);            }        });        holder.Description.setOnLongClickListener(new View.OnLongClickListener() {            @Override            public boolean onLongClick(View v) {                setDelete_diolog(messages.getId());                Toast.makeText(context, "deleted data ", Toast.LENGTH_SHORT).show();                return true;            }        });    }    @Override    public int getItemCount() {        return messages_arrayList.size();    }    static class Message_ViewHolder extends RecyclerView.ViewHolder {        TextView Title;        TextView Description;        Message_ViewHolder(@NonNull View itemView) {            super(itemView);            Title = itemView.findViewById(R.id.out_title);            Description = itemView.findViewById(R.id.out_body);        }    }    public void setDelete_diolog(String position) {        /*         * this is the in build dialog box         * */        AlertDialog.Builder builder = new AlertDialog.Builder(context);        builder.setTitle("Alart Dialog!!");        builder.setMessage("Do you want to delete ??");        /*         * if user click yes then finish the activity         * */        builder.setPositiveButton("Yes", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                DatabaseManegar databaseManeger = new DatabaseManegar(context);                databaseManeger.deletData(position);                Toast.makeText(context, "deleted data ", Toast.LENGTH_SHORT).show();                dialog.dismiss();            }        });        /*         * if user click no then dialog box is canceled.         * */        builder.setNegativeButton("No", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                dialog.dismiss();            }        });        /*         * A null listener allows the button to dismiss the dialog and take no further action.         * */        builder.setCancelable(false);        /*         * create dialog box         * */        AlertDialog dialog = builder.create();        dialog.show();    }}