package com.example.basic_practice.helper;import android.os.Parcelable;import java.io.Serializable;/*public class Messages implements Serializable {* will be use for when class obj passing through intent* */public class Messages {    String Title;    String Body;    String id;    public Messages(String id, String Title, String Body) {        this.Title = Title;        this.Body = Body;        this.id = id;    }    public String getTitle() {        return Title;    }    public String getBody() {        return Body;    }    public void setTitle(String Title) {        this.Title = Title;    }    public void setBody(String Body) {        this.Body = Body;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }}