package com.example.basic_practice.helper;import android.content.Context;import android.content.Intent;import android.util.DisplayMetrics;import android.util.Log;import android.util.TypedValue;import android.view.Display;import android.view.WindowManager;import android.widget.Toast;public class Utils {    /*     * get display size in pixel unit     * it return an array of size 2     * arrry[0] = width     * array[1] = height     *     * */    public int[] get_display_size(Context context) {         /* previous metrix value output:        DisplayMetrics{density=0.0, width=0, height=0, scaledDensity=0.0, xdpi=0.0, ydpi=0.0} */        /* after the matrix value, metrix= DisplayMetrics{density=2.625, width=1080, height=2201,         scaledDensity=2625, xdpi=420 .0, ydpi=420.0} */        WindowManager manager = (WindowManager) context.getSystemService(Context.WINDOW_SERVICE);        Display display = manager.getDefaultDisplay();        DisplayMetrics metrics = new DisplayMetrics();        display.getMetrics(metrics);        float DISPLAY_WIDTH = metrics.widthPixels;        float DISPLAY_HEIGHT = metrics.heightPixels;        float Display_density = metrics.density;        int[] a = {(int) DISPLAY_WIDTH, (int) DISPLAY_HEIGHT};        return a;    }    /*     * Intent passing function to start a new activity     * it takes two parameters     *  context = from where to start     * layout = to where to go     *     * */    public Intent Intent_passing(Context context, Class layout, String key, String val) {        Intent intent = new Intent(context, layout);        intent.putExtra(key, val);        context.startActivity(intent);        return intent;    }    /*     * diolog function */}